mapper.select:
select count(*) from (select branchid,guid from svmeetinginf t where t.branchid in (select deptid from ptdept start with deptid =#{branchid} connect by prior deptid = parentdeptid)) a, svmeetingevent b where b.meetingid=a.guid and b.eventtype='1'
and b.txndate >= #{startdate} and b.txndate <= #{enddate}
order by a.branchid,b.txndate

select (select deptname from ptdept where deptid = a.branchid) as remark, b.txndate, b.eveventcontent, b.reason, b.verbal, b.proposal from (select branchid,guid from svmeetinginf t where t.branchid in (select deptid from ptdept start with deptid =#{branchid} connect by prior deptid = parentdeptid)) a, svmeetingevent b where b.meetingid=a.guid and b.eventtype='1'
and b.txndate >= #{startdate} and b.txndate <= #{enddate}
order by a.branchid,b.txndate

select (select deptname from ptdept where deptid = a.branchid) as remark, b.txndate, b.eveventcontent, b.reason, b.verbal, b.proposal from (select branchid,guid from svmeetinginf t where t.branchid in (select deptid from ptdept start with deptid =#{branchid} connect by prior deptid = parentdeptid)) a, svmeetingevent b where b.meetingid=a.guid and b.eventtype='2' and b.seqno=2
and b.txndate >= #{startdate} and b.txndate <= #{enddate}
order by a.branchid,b.txndate

select (select deptname from ptdept where deptid = a.branchid) as remark, b.txndate, b.eveventcontent, b.reason, b.verbal, b.proposal from (select branchid,guid from svmeetinginf t where t.branchid in (select deptid from ptdept start with deptid =#{branchid} connect by prior deptid = parentdeptid)) a, svmeetingevent b where b.meetingid=a.guid and b.eventtype='2' and b.seqno=3 and b.eveventcontent is not null
and b.txndate >= #{startdate} and b.txndate <= #{enddate}
order by a.branchid,b.txndate

select (select deptname from ptdept where deptid = a.branchid) as remark, b.txndate, b.eveventcontent, b.reason, b.verbal, b.proposal from (select branchid,guid from svmeetinginf t where t.branchid in (select deptid from ptdept start with deptid =#{branchid} connect by prior deptid = parentdeptid)) a, svmeetingevent b where b.meetingid=a.guid and b.eventtype='2' and b.seqno = 4 and b.eveventcontent is not null
and b.txndate >= #{startdate} and b.txndate <= #{enddate}
order by a.branchid,b.txndate
