sql.where:
          a.base_branch_id in (select deptid from ptdept start with deptid = #{branchId}  connect by prior deptid = parentdeptid)

          
              and a.rpt_type = #{rptType}
          
          
              and a.rpt_date = #{rptDate}
          
          
              and a.base_custno_ecif like '%${custNo}%'
          
          
              
              and a.asset_aum_tp >= #{startAum}
              
          
          
              
              and a.asset_aum_tp <= #{endAum}

mapper.select:
select  *
    from cls_rptdata a
  where 1=1
    
        and a.cert_type = #{certType}
    
    
        and a.cert_no = #{certNo}
    
order by a.rpt_date desc, a.rpt_type

select * from cls_rptdata a where 1=1
and a.base_custno_ecif = #{custNo}
order by a.rpt_date desc, a.rpt_type

 select *
   from (select t1.*, rownum rnum
         from (
             select  *
                 from cls_rptdata a
               
             order by
                
                     ${sortField}
                
                
                     ${sortOrder}
                
 ) t1
 
 where rownum <= #{pagesize}) t2
where t2.rnum > #{offset}

mapper.sql:
select a.*, b.aum_month_curr, b.aum_month_last, b.aum_timepoint_dep, b.loan_bal, b.cts_margin_bal, (select opername from ptoper where operid=a.custmgr_id) as custmgr_name from SV_CMS_CUSTBASE a, (select t.base_branch_id as branch_id, t.base_custno_ecif as cust_no, max(t.asset_aum_mad) as aum_month_curr, max(t.asset_aum_lm_mad) as aum_month_last, max(t.asset_aum_tp) as aum_timepoint_dep, max(t.loan_bal) as loan_bal, max(t.cts_bal_margin) as cts_margin_bal from cls_rptdata t where t.rpt_type = #{rptType} and t.rpt_date = #{rptDate} group by t.base_branch_id, t.base_custno_ecif) b where a.branch_id = b.branch_id and a.cust_no = b.cust_no and a.branch_id = #{branchId}
and a.custmgr_id is null
and a.custmgr_id = #{custmgrId}
and a.cust_no like '%${custNo}%'
and cust_name in
#{item}

