sql.where:
          a.delete_flag = '0'
          and a.branch_id in (select deptid from ptdept start with deptid = #{branchId}  connect by prior deptid = parentdeptid)

          
              and a.rpt_type = #{rptType}
          
          
              and a.rpt_date = #{rptDate}
          
          
              and a.cert_type = #{certType}
          
          
              and a.cert_no like '%${certNo}%'
          
          
              and a.cust_no like '%${custNo}%'
          
          
              
              and a.aum_timepoint_dep >= #{startAum}
              
          
          
              
              and a.aum_timepoint_dep <= #{endAum}

mapper.select:
select  *
    from sv_cls_custinfo a
  where 1=1
    
        and a.cert_type = #{certType}
    
    
        and a.cert_no = #{certNo}
    
order by a.rpt_date desc, a.rpt_type

 select *
   from (select t1.*, rownum rnum
         from (
             select  *
                 from sv_cls_custinfo a
               
             order by
                
                     ${sortField}
                
                
                     ${sortOrder}
                
 ) t1
 
 where rownum <= #{pagesize}) t2
where t2.rnum > #{offset}

mapper.sql:
select a.*, b.aum_month_curr, b.aum_month_last, b.aum_timepoint_dep, b.loan_bal, b.cts_margin_bal, (select opername from ptoper where operid=a.custmgr_id) as custmgr_name from SV_CMS_CUSTBASE a, (select t.branch_id, t.cert_type, t.cert_no, max(t.aum_month_curr) as aum_month_curr, max(t.aum_month_last) as aum_month_last, max(t.aum_timepoint_dep) as aum_timepoint_dep, max(t.loan_bal) as loan_bal, max(t.cts_margin_bal) as cts_margin_bal from sv_cls_custinfo t where t.rpt_type = #{rptType} and t.rpt_date = #{rptDate} group by t.branch_id, t.cert_type, t.cert_no) b where a.branch_id = b.branch_id and a.cert_type = b.cert_type and a.cert_no = b.cert_no and a.branch_id = #{branchId}
and a.custmgr_id is null
and a.custmgr_id = #{custmgrId}
and cust_name in
#{item}

