mapper.sql:
select a.*,
   (select opername from ptoper where operid=a.custmgr_id) as custmgr_name
from SV_CMS_CUSTBASE a
    where a.branch_id = #{branchId}
        
            and a.custmgr_id is null
        
        
            and a.custmgr_id = #{custmgrId}
        
        
            and cust_name in
            
                #{item}

