mapper.select:
select count(*)
  from sv_sale_detail a, ptoper b
where a.tellerid = b.operid
    and b.deptid in (select deptid from ptdept start with deptid = #{branchId}  connect by prior deptid = parentdeptid)
    and a.txndate between #{startDate} and #{endDate}

        and a.prdid = #{prdId}


        and a.tellerid = #{tellerId}

select * from (select t1.*, rownum rnum from ( select b.deptid, (select deptname from ptdept where deptid = b.deptid) as deptname, b.opername as tellername, (select enuitemlabel from ptenudetail where enutype = 'SVTPRDTYPE' and enuitemvalue = a.prdid) as prdname, (case a.prdid when '01' then (select enuitemlabel from ptenudetail where enutype = 'SVTFUNDTYPE' and enuitemvalue = a.subprdid) when '02' then (select enuitemlabel from ptenudetail where enutype = 'SVTINSURETYPE' and enuitemvalue = a.subprdid) when '03' then (select enuitemlabel from ptenudetail where enutype = 'SVTFPTYPE' and enuitemvalue = a.subprdid) when '06' then (select enuitemlabel from ptenudetail where enutype = 'SVTCREDITCARDTYPE' and enuitemvalue = a.subprdid) when '08' then (select enuitemlabel from ptenudetail where enutype = 'SVTEBANKTYPE' and enuitemvalue = a.subprdid) when '09' then (select enuitemlabel from ptenudetail where enutype = 'SVTGOLDTYPE' and enuitemvalue = a.subprdid) end) as subprdname, a.*,c.cust_name as custname, decode(d.checkflag,'1','未通过','2','通过','未检核') as checkflag from sv_sale_detail a, ptoper b, sv_cms_custbase c, sv_sale_detail_chk d where a.tellerid = b.operid and b.deptid in (select deptid from ptdept start with deptid = #{branchId} connect by prior deptid = parentdeptid) and a.txndate between #{startDate} and #{endDate} and a.custguid = c.guid(+)
and a.prdid = #{prdId}
and a.tellerid = #{tellerId}
and a.guid = d.guid(+) order by b.deptid, a.tellerid, a.prdid, a.subprdid, a.txndate ) t1
where rownum <= #{pagesize}) t2 where t2.rnum > #{offset}
