mapper.select:
select count(*)
  from sv_sale_detail a, ptoper b, sv_cms_custbase c, sv_sale_detail_chk d
where a.tellerid = b.operid
    and b.deptid in (select deptid from ptdept start with deptid = #{branchId}  connect by prior deptid = parentdeptid)
    and a.txndate between #{startDate} and #{endDate}
    and a.custguid = c.guid(+)
    and a.guid = d.guid(+)

        and a.prdid = #{prdId}


        and a.tellerid = #{tellerId}


        and (case when  d.checkflag is null then '0' else d.checkflag end) = #{checkFlag}


        and d.archiveFlag = #{archiveFlag}


        and d.reviewFlag = #{reviewFlag}

select * from (select t1.*, rownum rnum from ( select b.deptid, (select deptname from ptdept where deptid = b.deptid) as deptname, b.opername as tellername, (select enuitemlabel from ptenudetail where enutype = 'SVTPRDTYPE' and enuitemvalue = a.prdid) as prdname, (case a.prdid when '01' then (select enuitemlabel from ptenudetail where enutype = 'SVTFUNDTYPE' and enuitemvalue = a.subprdid) when '02' then (select enuitemlabel from ptenudetail where enutype = 'SVTINSURETYPE' and enuitemvalue = a.subprdid) when '03' then (select enuitemlabel from ptenudetail where enutype = 'SVTFPTYPE' and enuitemvalue = a.subprdid) when '06' then (select enuitemlabel from ptenudetail where enutype = 'SVTCREDITCARDTYPE' and enuitemvalue = a.subprdid) when '08' then (select enuitemlabel from ptenudetail where enutype = 'SVTEBANKTYPE' and enuitemvalue = a.subprdid) when '09' then (select enuitemlabel from ptenudetail where enutype = 'SVTGOLDTYPE' and enuitemvalue = a.subprdid) end) as subprdname, a.*,c.cust_name as custname, d.checkflag, decode(d.checkflag,'1','未通过','2','通过','未检核') as checkflagDesc, d.checkdate,d.checktime,d.checktimes,d.checklog, d.checkoperid, (select opername from ptoper where operid=d.checkoperid) as checkopername, d.reviewflag, decode(d.reviewflag,'1','已申请','未申请') as reviewflagDesc, d.reviewdesc, decode(d.reviewresult,'0','通过','1','驳回','') as reviewresult, d.reviewreplydesc, d.archiveflag, decode(d.archiveflag,'0','未通过','1','已归档','') as archiveflagDesc, d.archivedate,d.archiveoperid,d.archiveoperdate from sv_sale_detail a, ptoper b, sv_cms_custbase c, sv_sale_detail_chk d where a.tellerid = b.operid and b.deptid in (select deptid from ptdept start with deptid = #{branchId} connect by prior deptid = parentdeptid) and a.txndate between #{startDate} and #{endDate} and a.custguid = c.guid(+) and a.guid = d.guid(+)
and a.prdid = #{prdId}
and a.tellerid = #{tellerId}
and (case when d.checkflag is null then '0' else d.checkflag end) = #{checkFlag}
and d.archiveFlag = #{archiveFlag}
and d.reviewFlag = #{reviewFlag}
order by b.deptid, a.tellerid, a.prdid, a.subprdid, a.txndate ) t1
where rownum <= #{pagesize}) t2 where t2.rnum > #{offset}
