mapper.select:
 select count(a.guid)
 from svprdsalinf a, ptoper b
where a.tellerid = b.operid
  and b.deptid in (select deptid from ptdept start with deptid = #{branchid}  connect by prior deptid = parentdeptid)
  and a.txndate between #{startdate} and #{enddate}
  
       and a.prdid = #{prdid}

select b.deptid, (select deptname from ptdept where deptid = b.deptid) as deptname, b.opername as tellername, (select enuitemlabel from ptenudetail where enutype = 'SVTPRDTYPE' and enuitemvalue = a.prdid) as prdname, (case a.prdid when '01' then (select enuitemlabel from ptenudetail where enutype = 'SVTFUNDTYPE' and enuitemvalue = a.subprdid) when '02' then (select enuitemlabel from ptenudetail where enutype = 'SVTINSURETYPE' and enuitemvalue = a.subprdid) when '03' then (select enuitemlabel from ptenudetail where enutype = 'SVTFPTYPE' and enuitemvalue = a.subprdid) when '06' then (select enuitemlabel from ptenudetail where enutype = 'SVTCREDITCARDTYPE' and enuitemvalue = a.subprdid) when '08' then (select enuitemlabel from ptenudetail where enutype = 'SVTEBANKTYPE' and enuitemvalue = a.subprdid) when '09' then (select enuitemlabel from ptenudetail where enutype = 'SVTGOLDTYPE' and enuitemvalue = a.subprdid) end) as subprdname, a.* from svprdsalinf a, ptoper b where a.tellerid = b.operid and b.deptid in (select deptid from ptdept start with deptid = #{branchid} connect by prior deptid = parentdeptid) and a.txndate between #{startdate} and #{enddate}
and a.prdid = #{prdid}
order by b.deptid, a.tellerid, a.prdid, a.subprdid, a.txndate

 select count(a.guid)
 from svprdsalplan a, ptoper b
where a.tellerid = b.operid
  and b.deptid in (select deptid from ptdept start with deptid = #{branchid}  connect by prior deptid = parentdeptid)
  and a.txndate between #{startdate} and #{enddate}
  
       and a.prdid = #{prdid}

